	public static void retrieveChampions() {
		Gson gs = new Gson();
		try {
			champions = gs.fromJson(backupChampions, Champions.class);
		} catch (Exception e) {
			Log.e("GameData", "Error parsing champions Json.", e);
		}
	}

	public static void retrieveItems() {
		Gson gs = new Gson();
		try {
			items = gs.fromJson(backupItems, Items.class);
		} catch (Exception e) {
			Log.e("GameData", "Error parsing items Json.", e);
		}
	}

